function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
var d = new Date();
var val = new Date(newValue); 
//alert('regDate_Year: ' + val.getFullYear() + ' & regDate_Mon: ' +  val.getMonth() + ' & regDate_date: ' +  val.getDate() );
//alert('2Date_Year: ' + d.getFullYear() + ' & 2Date_Mon: ' +  d.getMonth() + ' & 2Date_date: ' +  d.getDate() );
	if(val.getFullYear() < d.getFullYear())
		{
			alert("Registeration date should be greater than today's date");
			g_form.setValue('regdate','');
		}
	else
		{
			if ((val.getFullYear() == d.getFullYear()) && (val.getMonth() < d.getMonth()))
			{
				alert("Registeration date should be greater than today's date");
				g_form.setValue('regdate','');
			}
			else
			{
				if ((val.getFullYear() == d.getFullYear()) && (val.getMonth() == d.getMonth()) && (val.getDate() < d.getDate()))
					{
						alert("Registeration date should be greater than today's date");
						g_form.setValue('regdate','');
					}				 
			}
		}
   //Type appropriate comment here, and begin script below

}
